-- Records of company_desc
-- ----------------------------
BEGIN;
INSERT INTO `company_desc` (`id`, `name`, `roundName`, `cityName`, `establishDate`, `img_url`, `company_url`, `brief`, `desc`, `phone`, `email`, `address`, `xn_href`) VALUES (1, '北京汽车', 'IPO', '北京市>顺义区', '2010-09-19 16:00:00.000000', 'https://api.xiniudata.com/file/d0b926be217411eaab4800163e03b331', 'http://www.baicmotor.com/', '乘用车制造商', '北京汽车是一家乘用车制造商，主要业务涵盖乘用车研发、制造、销售与售后服务，乘用车核心零部件生产、汽车金融、以及其他相关业务。乘用车品牌涵盖合资豪华乘用车、合资豪华商务用车、合资中高端乘用车以及自主品牌乘用车。乘用车业务目前通过北京品牌、北京奔驰、北京现代和福建奔驰四大整车板块开展。', '010-56635595', 'bqgs@baicmotor.com', '顺义区双河大街99号', 'W331NCE6');
INSERT INTO `company_desc` (`id`, `name`, `roundName`, `cityName`, `establishDate`, `img_url`, `company_url`, `brief`, `desc`, `phone`, `email`, `address`, `xn_href`) VALUES (2, '北京雷石科技', 'B轮', '北京市>朝阳区', '2006-06-04 16:00:00.000000', 'https://api.xiniudata.com/file/e36171a25d8b11e9adaf00163e03b331', 'http://www.thunder.com.cn', '视频VOD设备提供商', '北京雷石科技是视频VOD设备提供商，主要从事现代宽带技术领域和信息家电领域中嵌入式系统软件的研究与开发应用，研发了AirKTV等多款无线点歌应用，开创了微信点歌和用百度音乐，小米音乐等听歌软件点歌的先河，实现移动端点歌功能，还开发了最具人气的KTV手机应用——移动练歌房，实现了KTV线上线下的互动。', '010-51660862', 'niuxiaoxiao@thunder.com.cn', '北京市朝阳区天畅园8号楼3层8-307', 'bjlshkjThunderstone');
INSERT INTO `company_desc` (`id`, `name`, `roundName`, `cityName`, `establishDate`, `img_url`, `company_url`, `brief`, `desc`, `phone`, `email`, `address`, `xn_href`) VALUES (3, '奇安信', 'IPO', '北京市>西城区', '2014-06-15 16:00:00.000000', 'https://api.xiniudata.com/file/de44a4b6e02911e9892700163e03b331', 'https://www.qianxin.com/', '政府企业机构网络安全与信息安全管理产品提供商', '360天擎是一家政府企业机构网络安全与信息安全管理产品提供商，主要产品为360天擎终端安全管理系统，是360面向政府、企业、金融、医疗及制造业等大型企事业单位推出的集防病毒与终端安全管控于一体的解决方案。', '010-56509064', 'legal@qianxin.com', '北京市西城区新街口外大街28号102号楼3层332号', '1T1HUDGC');
INSERT INTO `company_desc` (`id`, `name`, `roundName`, `cityName`, `establishDate`, `img_url`, `company_url`, `brief`, `desc`, `phone`, `email`, `address`, `xn_href`) VALUES (4, '恒生电子', 'IPO', '杭州市>滨江区', '2000-12-12 16:00:00.000000', 'https://api.xiniudata.com/file/60b5daa05c0911e99d4a00163e03b331', 'http://www.hundsun.com', '金融科技服务商', '恒生电子是一家以“让金融变简单”为使命的金融科技公司。恒生聚焦金融行业，致力于为证券、期货、基金、信托、保险、银行、交易所、私募等机构提供整体解决方案和服务。', '0571-28828', 'investor@hundsun.com', '浙江省杭州市滨江区江南大道3588号2幢11层', 'hengshengdianzi22');
