DROP TABLE IF EXISTS `course_course`;
CREATE TABLE `course_course` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(100) NOT NULL,
  `video_url` varchar(200) NOT NULL,
  `cover_url` varchar(200) NOT NULL,
  `price` double NOT NULL,
  `duration` int(11) NOT NULL,
  `profile` longtext NOT NULL,
  `pub_time` datetime(6) NOT NULL,
  `category_id` int(11) NOT NULL,
  `teacher_id` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `course_course_category_id_0b2127b9_fk_course_coursecategory_id` (`category_id`),
  KEY `course_course_teacher_id_f04a37b5_fk_course_teacher_id` (`teacher_id`),
  CONSTRAINT `course_course_category_id_0b2127b9_fk_course_coursecategory_id` FOREIGN KEY (`category_id`) REFERENCES `course_coursecategory` (`id`),
  CONSTRAINT `course_course_teacher_id_f04a37b5_fk_course_teacher_id` FOREIGN KEY (`teacher_id`) REFERENCES `course_teacher` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

